import React, { useState } from "react";
import "../Styles/SecurityInfo.css";
import { validationSecInfo } from "../Functions/validationSecInfo";
import AlertTitle from "@material-ui/lab/AlertTitle";
import Collapse from "@material-ui/core/Collapse";
import Alert from "@material-ui/lab/Alert";
import api from "../../SharedComponents/APIConfig";

function SecurityInfo(props) {
    const [state, setState] = useState({
        contrasena: "",
        confirmacion: "",
    });

    const [openError, setOpenError] = React.useState(false);
    const [openSuccess, setOpenSuccess] = React.useState(false);
    const [message, setMsg] = React.useState("");

    function handleChange(event) {
        setState({ ...state, [event.target.name]: event.target.value });
    }

    async function updatePassword(ID_Usuario, contrasena) {
        var response = await fetch(api.url + "/changePassword", {
            method: "put",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ ID_Usuario, contrasena }),
        });

        return response.status;
    }

    return (
        <div className="securityContainer">
            <div className="alertSec">
                <Collapse in={openError}>
                    <Alert
                        onClose={() => {
                            setOpenError(false);
                        }}
                        variant="outlined"
                        severity="error"
                    >
                        <AlertTitle>
                            Error - Faltan llenar los siguientes campos
                        </AlertTitle>
                        {message}
                    </Alert>
                </Collapse>

                <Collapse in={openSuccess}>
                    <Alert
                        onClose={() => {
                            setOpenSuccess(false);
                        }}
                        variant="outlined"
                        severity="success"
                    >
                        <AlertTitle>
                            Contrase√±a cambiada exitosamente
                        </AlertTitle>
                    </Alert>
                </Collapse>
            </div>

            <div className="securityTitle">
                <div className="secTitle1">
                    <i className="fa fa-key"></i>
                </div>
                <div className="secTitle2">
                    <label>Cambiar contrase√±a</label>
                </div>
            </div>
            <div className="securityPWD">
                <label htmlFor="contrasena" className="inp">
                    <input
                        autoComplete="off"
                        id="contrasena"
                        type="password"
                        name="contrasena"
                        value={state.contrasena}
                        onChange={handleChange}
                        placeholder="&nbsp;"
                    />
                    <span className="label">Nueva contrase√±a</span>
                    <span className="focus-bg"></span>
                </label>
            </div>
            <div className="securityCPWD">
                <label htmlFor="confirmacion" className="inp">
                    <input
                        autoComplete="off"
                        id="confirmacion"
                        type="password"
                        name="confirmacion"
                        value={state.confirmacion}
                        onChange={handleChange}
                        placeholder="&nbsp;"
                    />
                    <span className="label">Confirmar contrase√±a</span>
                    <span className="focus-bg"></span>
                </label>
            </div>
            <div className="btnSecurityBlock">
                <input
                    autoComplete="off"
                    className="btnSecurity"
                    type="button"
                    value="Guardar"
                    onClick={async () => {
                        const valid = validationSecInfo(state);
                        if (valid.isValid) {
                            const status = await updatePassword(
                                props.ID_Usuario,
                                state.contrasena
                            );
                            if (status === 200) {
                                setOpenError(false);
                                setOpenSuccess(true);
                            } else {
                                valid.msg =
                                    "No se ha podido actualizar la contrase√±a. Error en el servidor.";
                                setMsg(valid.msg);
                                setOpenSuccess(false);
                                setOpenError(true);
                            }
                        } else {
                            setMsg(valid.msg);
                            setOpenSuccess(false);
                            setOpenError(true);
                        }
                    }}
                />
            </div>
        </div>
    );
}

export default SecurityInfo;
